    my $stSet2 = Set($student->[1]);
    my $test = $stSet1->isSubsetOf($stSet2);
    Value->Error("The sets must be different") if $stSet1 == $stSet2;
    $self->setMessage(2,"\(A\) is not a subset of \(B\)") if !test;
    return $test;
